import React from 'react';
import { MapContainer, TileLayer, GeoJSON } from 'react-leaflet';
import countriesData from './countries.geojson';

function WorldMap() {
  const onCountryClick = (event) => {
    console.log(event.target.feature.properties); // Ülke özelliklerini konsola yazdır
    // Burada ülke özelliklerini kullanarak istediğiniz işlemleri yapabilirsiniz
  };

  return (
    <MapContainer center={[0, 0]} zoom={2} style={{ height: '400px', width: '100%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <GeoJSON data={countriesData} onClick={onCountryClick} />
    </MapContainer>
  );
}

export default WorldMap;
