import React from 'react';
import { MapContainer, TileLayer, GeoJSON } from 'react-leaflet';
import { Link } from 'react-router-dom';
import 'leaflet/dist/leaflet.css';

function Map() {
  const geojsonData = require('./countries.geo.json'); // Dünya haritasının GeoJSON verisi

  const style = {
    weight: 1,
    opacity: 1,
    color: 'black',
    fillOpacity: 0.7,
    fillColor: '#3388ff'
  };

  const onEachCountry = (country, layer) => {
    layer.on('click', () => {
      window.location.href = `/country/${country.properties.name}`; // Ülke adını içeren URL'ye yönlendir
    });
  };

  return (
    <MapContainer style={{ height: '1000px', width: '100%' }} zoom={2} center={[0, 0]}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <GeoJSON data={geojsonData} style={style} onEachFeature={onEachCountry} />
    </MapContainer>
  );
}

export default Map;
