import React, { useState, useEffect } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { useParams } from 'react-router-dom';
import { ProgressSpinner } from 'primereact/progressspinner';
import { Dialog } from 'primereact/dialog';
import axios from 'axios'

 export const CountryInfo = () =>{
  const { name } = useParams(); // URL'den ülke adını al
  const [datas, setDatas] = useState([]);
  const [ regionName, setRegionName ]= useState(null);
  const [loading, setLoading] = useState(true);
  const [visible, setVisible] = useState(false);
  useEffect(() => {
    fetch();
  },[name]);
  const fetch =  async () => {
    const options = {
      method: 'GET',
      url: 'https://covid-19-statistics.p.rapidapi.com/reports',
      params: {iso: name},
      headers: {
        'X-RapidAPI-Key': 'bf912b2ac4mshdabf19e77d8742bp123a75jsn5e3d3f2c9b6b',
        'X-RapidAPI-Host': 'covid-19-statistics.p.rapidapi.com'
      }
    };
    try {
      setVisible(true);
    const response = await axios.request(options);
    setDatas(response.data.data)
    setRegionName(response.data.data[0].region.name)
    setVisible(false);
    console.log(response.data.data);
  } catch (error) {
    console.error(error);
  }
  }

  return (
    <div>
      <h1>{regionName}</h1> {/* Ülke adını başlık olarak göster */}
      <div className="card">
            <DataTable value={datas} tableStyle={{ minWidth: '50rem' }}>
                <Column field="active" header="Active"></Column>
                <Column field="confirmed" header="Confirmed"></Column>
                <Column field="deaths" header="Deaths"></Column>
                <Column field="recovered" header="Decovered"></Column> 
            </DataTable>
        </div>
        <Dialog header="Header" visible={visible} style={{ width: '50vw' }} onHide={() => setVisible(false)}>
                <p className="m-0">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. 
                    Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo
                    consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. 
                    Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                </p>
            </Dialog>
    </div>
  );
}
